import React, { Component } from "react";
// import { Link, withRouter } from "react-router-dom";
import 'materialize-css/dist/css/materialize.min.css';
import M from 'materialize-css/dist/js/materialize.min.js';

document.addEventListener('DOMContentLoaded', function() {
    var elem = document.querySelectorAll('select');
    var options={}
    var instances = M.FormSelect.init(elem, options);
    var instance = M.FormSelect.getInstance(elem);
  });

document.addEventListener('DOMContentLoaded', function() {
  var elems = document.querySelectorAll('.datepicker');
  var options= {
      format: "dd-mm-yyyy",
      yearRange: 30 
  }
  var instances = M.Datepicker.init(elems, options);
});

class Profile extends Component {
    constructor() {
        super();
        this.state = {
            firstName: "",
            lastName: "",
            collegeName: "",
            gradYear: "",
            gender: "",
            dob: "",
            mobileNo: "",
            profilePic: "",
            interests: [],
            errors: {}
        };
    }

    onChange = e => {
        this.setState({ [e.target.id]: e.target.value});
    };

    onSubmit = e => {
        e.preventDefault();

        const newProfile = {
            firstName: this.state.firstName,
            lastName: this.state.lastName,
            collegeName: this.state.collegeName,
            gradYear: this.state.gradYear,
            gender: this.state.gender,
            dob: this.state.dob,
            mobileNo: this.state.mobileNo,
            profilePic: this.state.profilePic,
            interests: this.state.interests
            // interests: this.state.interests.map(item => (
            //     key={item}>{item}))
        };
        console.log(newProfile);
    };

    render() {
        const { errors } = this.state;
        
        return(
            <div className="container">
                <div className="row">
                    <form noValidate onSubmit={this.onSubmit}>
                        <div className="input-field col s12 m6">
                            <input
                                onChange={this.onChange}
                                value={this.state.firstName}
                                error={this.state.firstName} 
                                id="firstName" 
                                type="text"
                            />
                            <label htmlFor="firstName">First Name</label>
                        </div>
                        <div className="input-field col s12 m6">
                            <input
                                onChange={this.onChange}
                                value={this.state.lastName}
                                error={this.state.lastName} 
                                id="lastName"
                                type="text"
                            />
                            <label htmlFor="lastName">Last Name</label>
                        </div>
                        <div className="input-field col s12">
                            <input
                                onChange={this.onChange}
                                value={this.state.collegeName}
                                error={this.state.collegeName} 
                                id="collegeName"
                                type="text"
                            />
                            <label htmlFor="collegeName">College Name</label>
                        </div>
                        <div className="input-field col s12 m6">
                            <select>
                                <option value="" disabled selected defaultValue>Choose your graduation year</option>
                                <option value={this.state.gradYear}>2021</option>
                                <option value={this.state.gradYear}>2022</option>
                                <option value={this.state.gradYear}>2023</option>
                                <option value={this.state.gradYear}>2024</option>
                            </select>
                            <label htmlFor="gradYear">Graduation Year</label>
                        </div>
                        <div className="input-field col s12 m6">
                            <input
                                onChange={this.onChange}
                                value={this.state.dob}
                                error={this.state.dob}
                                id="dob"
                                type="text"
                                className="datepicker"
                            />
                            <label htmlFor="dob">Date of birth</label>
                        </div>
                        <div className="input-field col s12 m6">
                            <select>
                                <option value="" disabled selected defaultValue>Choose Gender</option>
                                <option value={this.state.gender}>Male</option>
                                <option value={this.state.gender}>Female</option>
                                <option value={this.state.gender}>Can't Say</option>
                            </select>
                            <label htmlFor="gender">Gender</label>
                        </div>
                        <div className="input-field col s12 m6">
                            <input
                                onChange={this.onChange}
                                value={this.state.mobileNo}
                                error={this.state.mobileNo}
                                id="mobileNo"
                                type="text"
                            />
                            <label htmlFor="mobileNo">Mobile No.</label>
                        </div>
                        <div className="input-field col s12">
                            <select multiple>
                                {/* <option value="" disabled selected>Choose your interests</option> */}
                                <option value={this.state.interests}>Machine Learning</option>
                                <option value={this.state.interests}>Blockchain</option>
                                <option value={this.state.interests}>Full-Stack</option>
                            </select>
                            <label htmlFor="interests">Interests</label>
                        </div>
                        <div className="col s12" style={{ paddingLeft: "11.250px" }}>
                            <button
                                style={{
                                    width: "250px",
                                    borderRadius: "3px",
                                    letterSpacing: "1.5px",
                                    marginTop: "1rem"
                                }}
                                type= "submit"
                                className="btn btn-large waves-effect waves-light hoverable blue accent-3"
                                >
                                Save your profile
                            </button>
                        </div>
                    </form>
                </div>
            </div>
        );
    }
}

export default Profile;
